<?php if ( ! defined('BASEPATH')) exit('No direct script access allowed');class M_help extends CI_Model {			function nltobr($str){		$text=strip_tags(nl2br($str),'<br>');		return $text;	}	function get_url_host($url){			$data = parse_url($url);		$data = $data['host'];		return $data;	}		function nice_time($date){		date_default_timezone_set('Asia/Shanghai');		$str = '';		$timer = strtotime($date);		$diff = time()- $timer;		$day = floor($diff / 86400);		$free = $diff % 86400;		if($day > 0) {		return $day."天前";		}else{		if($free>0){		$hour = floor($free / 3600);		$free = $free % 3600;		if($hour>0){		return $hour."小时前";		}else{		if($free>0){		$min = floor($free / 60);		$free = $free % 60;		if($min>0){		return $min."分钟前";		}else{		if($free>0){		return $free."秒前";		}else{		return '刚刚';		}		}		}else{		return '刚刚';		}		}		}else{		return '刚刚';		}		}	}}